import './App.css';
import Web3Modal from 'web3modal';
import { ethers } from 'ethers';
import { useState, useEffect, useLocalStorage } from 'react';
import {bankContract, tokenContract} from './contract-address.json';
import Bank from './artifacts/contracts/Bank.sol/Bank.json';
import Token from './artifacts/contracts/Token.sol/Token.json';


function App() {
  //States
  const [connectedACC, setConnectedACC, ] = useLocalStorage("address", "connect to wallet");
  const [vault , setVault] = useState();
  const [userAssets, setUserAssets] = useState();
  useEffect(() => {
    loadVault()
  }, [])

  async function loadVault() {
    const provider = new ethers.providers.JsonRpcProvider();
    const bankInfo = new ethers.Contract(bankContract, Bank.abi, provider);
    const tokenInfo = new ethers.Contract(tokenContract, Token.abi, provider);
    const bankAssets = ethers.utils.formatEther(await bankInfo.totalAssets(), 'ether');
    console.log(bankAssets)
  }

  //Connect to wallet
  async function connectToWallet() {
    const web3Modal = new Web3Modal();
    const connection = await web3Modal.connect();
    const provider = new ethers.providers.Web3Provider(connection)
    setConnectedACC(connection.selectedAddress);
  }


  return (
    <div className="">
      <header className="">
        <h1 className="">DA BANK</h1>
        <p>A decentralized bank</p>
        <button onClick={() => connectToWallet()}>{connectedACC}</button>
      </header>
    </div>
  );
}

export default App;
